mapper.select:

SELECT DECODE(COUNT(*), 0, 1, 2)
  FROM TB_MEMCART 
 WHERE p_no = #{p_no}
   AND mem_id = #{mem_id}

SELECT CART_COUNT FROM TB_MEMCART
 WHERE MEM_ID = #{mem_id}
   AND p_no = #{p_no}

mapper.update:
UPDATE (
        SELECT cart_count FROM TB_MEMCART 
         WHERE mem_id = #{mem_id}
           AND p_no = #{p_no}
       )

   	SET CART_COUNT = CART_COUNT-1


   	SET CART_COUNT = CART_COUNT+1


	SET CART_COUNT = CART_COUNT + #{cart_count}

mapper.delete:
DELETE FROM TB_MEMCART 
 WHERE mem_id = #{mem_id}
   AND p_no = #{p_no}

DELETE tb_memcart
WHERE mem_id = #{mem_id}
  AND p_no in

	#{item}
